const hamb=document.querySelector("#burger"),popup=document.querySelector("#popup"),intro=document.querySelector("#intro"),body=document.body,menu=document.querySelector("#menu").cloneNode(1),menu2=document.querySelector("#menu2").cloneNode(1),logo=document.querySelector("#logo").cloneNode(1);function hambHandler(e){e.preventDefault(),popup.classList.toggle("open"),hamb.classList.toggle("active"),body.classList.toggle("noscroll"),renderPopup()}function renderPopup(){popup.appendChild(menu),popup.appendChild(menu2)}hamb.addEventListener("click",hambHandler);const links=[...Array.from(menu.children),...Array.from(menu2.children)];function closeOnClick(){popup.classList.remove("open"),hamb.classList.remove("active"),body.classList.remove("noscroll")}links.forEach((e=>{e.addEventListener("click",closeOnClick)}));